import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Paper,
  TextField,
  IconButton,
  Typography,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  AppBar,
  Toolbar,
  Drawer,
  ListItemButton,
  Avatar,
} from '@mui/material';
import {
  Send as SendIcon,
  ExitToApp as LogoutIcon,
  Menu as MenuIcon,
} from '@mui/icons-material';
import io from 'socket.io-client';
import axios from 'axios';

// API Configuration
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const Chat = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [socket, setSocket] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const messagesEndRef = useRef(null);
  const currentUser = {
    id: localStorage.getItem('userId'),
    username: localStorage.getItem('username'),
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Fetch users from API
  const fetchUsers = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      const response = await axios.get(`${API_URL}/api/users`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      
      // Filter out current user
      const filteredUsers = response.data.filter(
        user => user.id !== currentUser.id && user._id !== currentUser.id
      );
      
      setUsers(filteredUsers);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  }, [currentUser.id]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    // Connect to socket with token for authentication
    const newSocket = io(API_URL, {
      auth: { token }
    });
    
    setSocket(newSocket);

    // Listen for users updates
    newSocket.on('users', (updatedUsers) => {
      // Filter out current user
      const filteredUsers = updatedUsers.filter(
        user => user.id !== currentUser.id
      );
      setUsers(filteredUsers);
    });

    newSocket.on('private message', (message) => {
      setMessages((prev) => [...prev, message]);
    });

    // Fetch initial users
    fetchUsers();

    return () => newSocket.close();
  }, [navigate, currentUser.id, fetchUsers]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedUser) return;

    const messageData = {
      recipientId: selectedUser.id,
      content: newMessage,
    };

    socket.emit('private message', messageData);
    setNewMessage('');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    localStorage.removeItem('username');
    navigate('/login');
  };

  // Toggle drawer on mobile
  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  // Determine if we're on mobile
  const isMobile = window.innerWidth < 768;

  return (
    <Box sx={{ display: 'flex', height: '100vh' }}>
      <AppBar position="static">
        <Toolbar>
          {isMobile && (
            <IconButton
              color="inherit"
              edge="start"
              onClick={toggleDrawer}
              sx={{ mr: 2 }}
            >
              <MenuIcon />
            </IconButton>
          )}
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Chat App
          </Typography>
          <IconButton color="inherit" onClick={handleLogout}>
            <LogoutIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      <Drawer
        variant={isMobile ? 'temporary' : 'permanent'}
        open={isMobile ? drawerOpen : true}
        onClose={isMobile ? toggleDrawer : undefined}
        sx={{
          width: 240,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: 240,
            boxSizing: 'border-box',
          },
        }}
      >
        <Toolbar />
        <Box sx={{ overflow: 'auto' }}>
          <List>
            <ListItem>
              <ListItemText primary="Users" />
            </ListItem>
            <Divider />
            {users.length > 0 ? (
              users.map((user) => (
                <ListItemButton
                  key={user.id || user._id}
                  selected={selectedUser?.id === user.id || selectedUser?._id === user._id}
                  onClick={() => setSelectedUser(user)}
                >
                  <ListItemIcon>
                    <Avatar>
                      {user.username ? user.username.charAt(0).toUpperCase() : 'U'}
                    </Avatar>
                  </ListItemIcon>
                  <ListItemText primary={user.username} />
                </ListItemButton>
              ))
            ) : (
              <ListItem>
                <ListItemText primary="No users available" />
              </ListItem>
            )}
          </List>
        </Box>
      </Drawer>

      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          display: 'flex',
          flexDirection: 'column',
          height: '100vh',
          pt: 8, // Add padding top to account for AppBar
        }}
      >
        <Paper
          elevation={3}
          sx={{
            flexGrow: 1,
            p: 2,
            display: 'flex',
            flexDirection: 'column',
            overflow: 'hidden',
          }}
        >
          {selectedUser ? (
            <>
              <Typography variant="h6" sx={{ mb: 2 }}>
                Chatting with {selectedUser.username}
              </Typography>
              <Box
                sx={{
                  flexGrow: 1,
                  overflow: 'auto',
                  mb: 2,
                }}
              >
                <List>
                  {messages.length > 0 ? (
                    messages.map((message, index) => (
                      <ListItem
                        key={index}
                        sx={{
                          justifyContent:
                            message.senderId === currentUser.id ? 'flex-end' : 'flex-start',
                        }}
                      >
                        <Paper
                          sx={{
                            p: 2,
                            backgroundColor:
                              message.senderId === currentUser.id
                                ? 'primary.main'
                                : 'grey.300',
                            color:
                              message.senderId === currentUser.id
                                ? 'primary.contrastText'
                                : 'text.primary',
                            maxWidth: '70%',
                          }}
                        >
                          <Typography variant="body1">{message.content}</Typography>
                          <Typography variant="caption" sx={{ display: 'block' }}>
                            {new Date(message.timestamp).toLocaleTimeString()}
                          </Typography>
                        </Paper>
                      </ListItem>
                    ))
                  ) : (
                    <Box sx={{ textAlign: 'center', mt: 4 }}>
                      <Typography variant="body1" color="textSecondary">
                        No messages yet. Start the conversation!
                      </Typography>
                    </Box>
                  )}
                  <div ref={messagesEndRef} />
                </List>
              </Box>
            </>
          ) : (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100%' }}>
              <Typography variant="h6" color="textSecondary">
                Select a user to start chatting
              </Typography>
            </Box>
          )}

          <Box
            component="form"
            onSubmit={handleSendMessage}
            sx={{
              display: 'flex',
              gap: 1,
            }}
          >
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Type a message..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              disabled={!selectedUser}
            />
            <IconButton
              color="primary"
              type="submit"
              disabled={!selectedUser || !newMessage.trim()}
            >
              <SendIcon />
            </IconButton>
          </Box>
        </Paper>
      </Box>
    </Box>
  );
};

export default Chat;
